mapper.select:
select seq, nickname, category, title, content, wdate from (select row_number()over(order by wdate desc) as rnum, seq, nickname, category, title, content, wdate from inq_bbs where nickname = #{nickname}
and category like concat('%', #{search} ,'%')
and title like concat('%', #{search} ,'%')
) a where rnum between ${start} and ${end}

select ifnull(count(*), 0) from inq_bbs where nickname = #{nickname}
and category like concat('%', #{search} ,'%')
and title like concat('%', #{search} ,'%')
