mapper.update:
UPDATE pt
SET title = #{title}, content = #{content}, filename = #{filename}, newfilename = #{newfilename}
WHERE seq = #{seq}

UPDATE pt
SET del = 1
WHERE seq = #{seq}

mapper.select:
SELECT COUNT(*)
FROM trainerlike
WHERE pt_seq=#{pt_seq} AND nickname=#{nickname}

mapper.update:
UPDATE pt
SET love = love + 1
WHERE seq = #{seq}

UPDATE pt
SET love = love - 1
WHERE seq = #{seq}
